import axios from "axios";

const URL = 'https://openweathermap.org/data/2.5/weather'
const API_key = 'cb382e81a2dc1fb71ad8d001367e403e'
export const fetchDataWeather = async (query) => {
    const {data} = await axios.get(URL,{
        params: {
            q: query,
            units:'metric',
            appid: API_key,
        }
    })

    return data
}
